mapper.update:
UPDATE QABOARD SET QA_TITLE=#{qaTitle}, 
   	   			   QA_CONTENT=#{qaContent}
 			 WHERE QA_NUM=#{qaNum}

UPDATE QABOARD 
   SET QA_VIEWCNT=QA_VIEWCNT+1 
 WHERE QA_NUM=#{qaNum}

mapper.delete:
DELETE COMMON_FILE 
 WHERE FILE_REF_BOARD='QABOARD' 
   AND FILE_REF_NUM=#{qaNum}

mapper.update:
UPDATE QABOARD 
   SET QA_STEP = QA_STEP+1 
 WHERE QA_REF = #{qaRef} 
   AND QA_STEP > #{qaStep}
